   const date = new Date(dateString);
   return `${months[date.getMonth()]}${date.getDate()}, ${date.getFullYear()}`
  }

  const renderTags = (tags) => {
    return tags.map(tag => {
      return(<span>{ tags.length > 1 ? `${tag}`: tag }</span>)
    })
  }

  const renderBlogs = () => {
     return blogs.map(blog => {
     return(<div key={blog._id}>
       <h3>{blog.title}</h3>
       <span>{ !blog.createAt ? null : renderDate(blog.createAt)}</span>
     <div>{renderTags(blog.tags)}</div>
       </div>)
    })
