  //  </button>
  <div class="col p-4 d-flex flex-column position-static">
          <strong class="d-inline-block mb-2 text-primary">{renderTags(post.tags)}</strong>
          <h3 class="mb-0">{post.title}</h3>
 <div class="mb-1 text-muted">{!post.createdAt ? null : renderDate(post.createdAt)}</div>
 <p class="card-text mb-auto">{ !post.excerpt ? null : post.excerpt}</p>
          <a href="#" class="stretched-link">Continue reading</a>
        </div>
 )
}

export default Post
