    <h3 class="mb-0">{post.title}</h3>
    <div class="mb-1 text-muted">{post.createdAt ? renderDate(post.createdAt) : null}</div>
      <p class="card-text mb-auto">{ !post.excerpt ? null : post.excerpt}</p>
      <Link class="stretched-link">Continue reading</Link>
  </button>
 )
}

export default Post
